import sys

def main():
    n = int(sys.stdin.readline())
    a = [int(i) for i in sys.stdin.readline().split()]
    dp = [[0, 0] for i in range(n)]
    dp[0][0] = 1
    dp[0][1] = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            dp[i][0] = dp[i - 1][0] + 1
            dp[i][1] = dp[i - 1][1] + 1
        elif a[i] < a[i - 1]:
            dp[i][0] = dp[i - 1][0]
            dp[i][1] = dp[i - 1][1] + 1
        else:
            dp[i][0] = dp[i - 1][0]
            dp[i][1] = dp[i - 1][1]
    print(max(dp[n - 1][0], dp[n - 1][1]))
    if dp[n - 1][0] > dp[n - 1][1]:
        i = n - 1
        while i > 0:
            if dp[i][0] == dp[i - 1][0] + 1 and a[i] > a[i - 1]:
                print('L', end='')
            else:
                print('R', end='')
            i -= 1
    else:
        i = n - 1
        while i > 0:
            if dp[i][1] == dp[i - 1][1] + 1 and a[i] > a[i - 1]:
                print('L', end='')
            else:
                print('R', end='')
            i -= 1
    print()

if __name__ == '__main__':
    main()
