n, k = map(int, input().split())
a = list(map(int, input().split()))

def check(c):
    for i in range(1, k+1):
        if c.count(i) < a.count(i):
            return False
    return True

def solve(n, k, a):
    c = [0] * n
    for i in range(n):
        c[i] = a[i]
    if check(c):
        return c
    c = [0] * n
    for i in range(n):
        c[i] = a[i] % k + 1
    if check(c):
        return c
    return None

c = solve(n, k, a)
if c is None:
    print("NO")
else:
    print("YES")
    print(*c)

import sys

def main():
    n, k = [int(x) for x in sys.stdin.readline().split()]
    a = [int(x) for x in sys.stdin.readline().split()]

    d = {}
    for i in range(n):
        if a[i] in d:
            d[a[i]].append(i)
        else:
            d[a[i]] = [i]

    if len(d) < k:
        print("NO")
        return

    ans = [0] * n
    for i in range(1, k + 1):
        if i <= len(d):
            for j in d[i]:
                ans[j] = i
        else:
            for j in d[i - k]:
                ans[j] = i

    print("YES")
    print(" ".join([str(x) for x in ans]))

if __name__ == "__main__":
    main()
