def main():
    n = int(input())
    a = input()
    f = list(map(int, input().split()))
    a = list(map(int, a))
    a.sort(reverse=True)
    b = sorted(f, reverse=True)
    for i in range(n):
        if a[i] == b[i]:
            continue
        else:
            a[i] = b[i]
            break
    print(''.join(map(str, a)))

if __name__ == "__main__":
    main()
