import sys

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    a.sort()
    if n == 2:
        print(0)
    else:
        print(min(a[n-1] - a[1], a[n-2] - a[0]))

if __name__ == '__main__':
    main()
