def solve(n, m, s, d, c):
    # If there are more exams than days, it is impossible
    if m > n:
        return -1

    # If there are more days than exams, it is possible
    if m <= n:
        schedule = [0] * n
        for i in range(m):
            for j in range(c[i]):
                schedule[s[i] + j - 1] = i + 1
            schedule[d[i] - 1] = m + 1
        return schedule

