def main():
    n = int(input())
    a = list(map(int, input().split()))
    if n == 1:
        print("YES")
        return
    if n == 2:
        if a[0] == a[1]:
            print("YES")
            return
        else:
            print("NO")
            return
    for i in range(1, n-1):
        if a[i] > a[i-1] and a[i] > a[i+1]:
            print("NO")
            return
    if a[0] > a[1]:
        print("NO")
        return
    if a[n-1] > a[n-2]:
        print("NO")
        return
    print("YES")

if __name__ == "__main__":
    main()

