def main():
    a, b, c = map(int, input().split())
    print(min(a+b+c, a+b+2*c, a+2*b+c, 2*a+b+c, 2*a+2*b+c, 2*a+2*b+3*c, 2*a+3*b+2*c, 3*a+2*b+2*c, 3*a+2*b+3*c, 3*a+3*b+2*c, 3*a+3*b+3*c))

if __name__ == "__main__":
    main()

def main():
    a,b,c = map(int,input().split())
    print(min(a,b,c)+min(a,b,c-1)+min(a,b-1,c-1)+min(a-1,b,c-1))

if __name__ == '__main__':
    main()

def main():
    a, b, c = [int(x) for x in input().split()]
    print(min(a, b, c) * 7 + min(a, b, c) * 3 + min(a, b, c) * 2)

if __name__ == "__main__":
    main()
