def solve(n, s):
    dp = [[0 for _ in range(n + 1)] for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(n):
        for j in range(n + 1):
            if j < n:
                dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % 1000000007
            if j > 0:
                dp[i + 1][j - 1] = (dp[i + 1][j - 1] + dp[i][j]) % 1000000007
