def main():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    i, j = 0, n-1
    s = 0
    while i <= j:
        if a[i] <= k:
            s += 1
            i += 1
        elif a[j] <= k:
            s += 1
            j -= 1
        else:
            break
    print(s)

if __name__ == '__main__':
    main()

