def solve(s):
    n = len(s)
    dp = [[0] * n for _ in range(n)]
    for i in range(n):
        dp[i][i] = 1
    for i in range(n - 1):
        if s[i] <= s[i + 1]:
            dp[i][i + 1] = 1
    for i in range(2, n):
        for j in range(n - i):
            if s[j] <= s[j + i]:
                dp[j][j + i] = 1
    for i in range(n - 1, -1, -1):
        for j in range(i - 1, -1, -1):
            if dp[j][i] == 1:
                for k in range(j + 1, i):
                    if dp[j][k] == 1 and dp[k + 1][i] == 1:
                        dp[j][i] = 2
                        break
    if dp[0][n - 1] == 1:
        return "YES"
    else:
        return "NO"

