def main():
    n, d, k = map(int, input().split())

    if d == 1:
        print("YES")
        for i in range(1, n):
            print(i, i + 1)
        return

    if d == 2:
        if n <= 2 * k + 1:
            print("YES")
            for i in range(1, n - 1):
                print(i, i + 1)
            print(n - 1, n)
        else:
            print("NO")
        return

    if d == 3:
        if n <= 3 * k + 1:
            print("YES")
            for i in range(1, n - 2):
                print(i, i + 1)
            print(n - 2, n - 1)
            print(n - 1, n)
        else:
            print("NO")
        return

    if d == 4:
        if n <= 4 * k + 1:
            print("YES")
            for i in range(1, n - 3):
                print(i, i + 1)
            print(n - 3, n - 2)
            print(n - 2, n - 1)
            print(n - 1, n)
        else:
            print("NO")
        return

    print("NO")

