def main():
    n = int(input())
    x = list(map(int, input().split()))
    x.sort()
    ans = 0
    for i in range(1, n):
        ans += x[i] - x[i-1]
    print(ans)

if __name__ == '__main__':
    main()

def main():
    n = int(input())
    x = list(map(int, input().split()))
    x.sort()
    if n % 2 == 0:
        print(x[n//2] - x[n//2 - 1])
    else:
        print(min(x[n//2] - x[n//2 - 1], x[n//2 + 1] - x[n//2]))

if __name__ == '__main__':
    main()
