import math

N = int(input())

# 一度に進めるマスの数
# 偶数なら2倍、奇数なら2倍+1
def move_count(n):
    if n % 2 == 0:
        return n // 2
    else:
        return n // 2 + 1

# 一度に進めるマスの数がN以上になる最小のn
def min_n(N):
    n = 1
    while move_count(n) < N:
        n += 1
    return n

# Nが置かれているマスの行
def row(N):
    n = min_n(N)
    if n % 2 == 0:
        return n
    else:
        return n + 1

# Nが置かれているマスの列
def col(N):
    n = min_n(N)
    if n % 2 == 0:
        return n
    else:
        return n - 1

# Nが置かれているマスの座標
def coord(N):
    return (row(N), col(N))

# 左上から右下までのマス数
def move_count_to_coord(N):
    r, c = coord(N)
    return r + c - 2

# 左上から右下までのマス数とNが置かれているマスの座標からNを置いているマスまでのマス数
def move_count_to_N(N):
    return move_count_to_coord(N) - (N - 1)

print(move_count_to_N(N))

