def main():
    n = int(input())
    a = list(map(int, input().split()))
    b = [0]*n
    for i in range(n):
        b[i] = a[i]
    b.sort()
    for i in range(n):
        if a[i] != b[i]:
            print("NO")
            return
    print("YES")

if __name__ == "__main__":
    main()

