import sys

def main():
    n, r = map(int, sys.stdin.readline().split())
    house = list(map(int, sys.stdin.readline().split()))
    heaters = 0
    for i in range(n):
        if house[i] == 1:
            heaters += 1
            continue
        if i == 0:
            if house[i+1] == 1:
                heaters += 1
                continue
        elif i == n-1:
            if house[i-1] == 1:
                heaters += 1
                continue
        else:
            if house[i-1] == 1 or house[i+1] == 1:
                heaters += 1
                continue
        for j in range(1, r):
            if i-j < 0:
                break
            if house[i-j] == 1:
                heaters += 1
                break
            if i+j >= n:
                break
            if house[i+j] == 1:
                heaters += 1
                break
        else:
            print(-1)
            return
    print(heaters)

if __name__ == "__main__":
    main()

