def main():
    n, r = [int(x) for x in input().split()]
    a = [int(x) for x in input().split()]
    cnt = 0
    for i in range(n):
        if a[i] == 1:
            cnt += 1
            continue
        for j in range(max(0, i-r+1), min(n, i+r)):
            if a[j] == 1:
                cnt += 1
                break
    if cnt == n:
        print(sum(a))
    else:
        print(-1)

if __name__ == "__main__":
    main()

def main():
    n, r = map(int, input().split())
    a = list(map(int, input().split()))
    heaters = []
    for i in range(n):
        if a[i] == 1:
            heaters.append(i)
    if len(heaters) == 0:
        print(-1)
        return
    if heaters[0] > r - 1:
        print(-1)
        return
    if heaters[-1] < n - r:
        print(-1)
        return
    heaters_needed = 1
    for i in range(1, len(heaters)):
        if heaters[i] - heaters[i - 1] > 2 * r - 1:
            heaters_needed += 1
    print(heaters_needed)

if __name__ == "__main__":
    main()
