n = int(input())
s = input()

open_brackets = 0
close_brackets = 0

for i in range(n):
    if s[i] == '(':
        open_brackets += 1
    else:
        if open_brackets > 0:
            open_brackets -= 1
        else:
            close_brackets += 1

print(open_brackets + close_brackets)

def main():
    n = int(input())
    s = input()
    count = 0
    for i in range(n):
        if s[i] == ')':
            count += 1
    if count == n:
        print(0)
    else:
        print(n - count)

if __name__ == '__main__':
    main()

n = int(input())
s = input()

def is_regular(s):
    stack = []
    for c in s:
        if c == '(':
            stack.append(c)
        else:
            if len(stack) == 0:
                return False
            else:
                stack.pop()
    return len(stack) == 0

def count_regular(s):
    count = 0
    for i in range(len(s)):
        if is_regular(s[:i] + '(' + s[i+1:]):
            count += 1
        if is_regular(s[:i] + ')' + s[i+1:]):
            count += 1
    return count

print(count_regular(s))

def main():
    n = int(input())
    s = input()
    if n == 1:
        print(0)
        return
    if s[0] == ')':
        print(0)
        return
    if s[n-1] == '(':
        print(0)
        return
    cnt = 0
    for i in range(1, n-1):
        if s[i] == ')':
            if s[i-1] == '(' and s[i+1] == ')':
                cnt += 1
        else:
            if s[i-1] == ')' and s[i+1] == '(':
                cnt += 1
    print(cnt)

if __name__ == '__main__':
    main()

import sys

def solve(s):
    n = len(s)
    s = list(s)
    ans = 0
    if s[0] == ')':
        ans += 1
        s[0] = '('
    for i in range(1, n):
        if s[i] == ')':
            if s[i-1] == ')':
                ans += 1
                s[i] = '('
    return ans

if __name__ == "__main__":
    n = int(sys.stdin.readline().strip())
    s = sys.stdin.readline().strip()
    print(solve(s))
