n = int(input())
a = list(map(int, input().split()))

# dp[i] = max number of problems in the contest with the last problem a[i]
dp = [1] * n

for i in range(1, n):
    for j in range(i):
        if a[i] <= 2 * a[j]:
            dp[i] = max(dp[i], dp[j] + 1)

print(max(dp))

import sys

def main():
    n = int(sys.stdin.readline().strip())
    a = list(map(int, sys.stdin.readline().strip().split()))
    dp = [1] * n
    for i in range(n):
        for j in range(i):
            if a[i] <= a[j] * 2:
                dp[i] = max(dp[i], dp[j] + 1)
    print(max(dp))

if __name__ == '__main__':
    main()

n = int(input())
a = list(map(int, input().split()))

# dp[i] = max number of problems in a valid contest ending at a[i]
dp = [1] * n

for i in range(n):
    for j in range(i):
        if a[j] * 2 >= a[i]:
            dp[i] = max(dp[i], dp[j] + 1)

print(max(dp))

n = int(input())
a = list(map(int, input().split()))

dp = [0] * n
dp[0] = 1

for i in range(1, n):
    if a[i] <= a[i - 1] * 2:
        dp[i] = dp[i - 1] + 1
    else:
        dp[i] = 1

print(max(dp))

def main():
    n = int(input())
    a = list(map(int, input().split()))
    dp = [0] * n
    dp[0] = 1
    for i in range(1, n):
        if a[i] <= a[i - 1] * 2:
            dp[i] = dp[i - 1] + 1
        else:
            dp[i] = 1
    print(max(dp))

if __name__ == '__main__':
    main()

def main():
    n = int(input())
    a = list(map(int, input().split()))
    ans = 1
    for i in range(1, n):
        if a[i] <= a[i - 1] * 2:
            ans += 1
        else:
            break
    print(ans)

if __name__ == "__main__":
    main()

