import sys

def main():
    n, m = map(int, sys.stdin.readline().split())
    a = [int(sys.stdin.readline()) for _ in range(m)]
    dp = [0] * (n + 1)
    dp[0] = 1
    for i in range(n):
        if i + 1 <= n:
            dp[i + 1] += dp[i]
        if i + 2 <= n:
            dp[i + 2] += dp[i]
        if i + 1 in a:
            dp[i + 1] = 0
        if i + 2 in a:
            dp[i + 2] = 0
    print(dp[n] % 1000000007)

if __name__ == '__main__':
    main()

