import sys

def min_packages(n, k):
    if n == 1:
        return 1
    if n <= k:
        return n
    if n % 2 == 0:
        return min_packages(n // 2, k) + 1
    else:
        return min(n, min_packages(n - 1, k) + 1)

def main():
    t = int(sys.stdin.readline().strip())
    for i in range(t):
        n, k = map(int, sys.stdin.readline().strip().split())
        print(min_packages(n, k))

if __name__ == '__main__':
    main()

