import sys
import re

def is_diverse(s):
    if len(s) == 1:
        return True
    if len(s) != len(set(s)):
        return False
    s = re.sub(r'[a-z]', lambda m: chr(ord(m.group(0)) - ord('a') + ord('A')), s)
    for i in range(len(s) - 1):
        if ord(s[i]) + 1 != ord(s[i + 1]):
            return False
    return True

n = int(sys.stdin.readline())
for i in range(n):
    s = sys.stdin.readline().strip()
    if is_diverse(s):
        print("Yes")
    else:
        print("No")

