def main():
    n = int(input())
    a = list(map(int, input().split()))
    b = [0] * n
    for i in range(n):
        b[a[i]-1] = i
    l = 0
    r = n-1
    ans = []
    while l <= r:
        if b[l] < b[r]:
            ans.append('L')
            l += 1
        else:
            ans.append('R')
            r -= 1
    print(len(ans))
    print(''.join(ans))

