n, a, b = map(int, input().split())

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return a * b // gcd(a, b)

def blue_balls(n, a, b):
    return n // lcm(a, b) * a

print(min(blue_balls(n, a, b), n))
