def main():
    n = int(input())
    a = list(map(int, input().split()))
    b = []
    for i in range(n):
        if a[i] not in b:
            b.append(a[i])
    print(len(b))
    print(*b)

if __name__ == '__main__':
    main()

def main():
    n = int(input())
    a = list(map(int, input().split()))
    a.reverse()
    a_unique = []
    for i in a:
        if i not in a_unique:
            a_unique.append(i)
    a_unique.reverse()
    print(len(a_unique))
    print(*a_unique)

if __name__ == '__main__':
    main()
