import sys

def main():
    a = int(sys.stdin.readline())
    while sum(map(int, list(str(a)))) % 4 != 0:
        a += 1
    print(a)

if __name__ == "__main__":
    main()

a = int(input())

if a % 4 == 0:
    print(a)
else:
    print(a + 4 - (a % 4))

def is_interesting(n):
    return sum(int(i) for i in str(n)) % 4 == 0

def main():
    a = int(input())
    while not is_interesting(a):
        a += 1
    print(a)

if __name__ == '__main__':
    main()

def sum_of_digits(n):
    s = 0
    while n:
        s += n % 10
        n //= 10
    return s

def is_interesting(n):
    return sum_of_digits(n) % 4 == 0

a = int(input())

while not is_interesting(a):
    a += 1

print(a)

def main():
    a = int(input())
    while True:
        if sum([int(i) for i in str(a)]) % 4 == 0:
            print(a)
            break
        a += 1

if __name__ == '__main__':
    main()

