import sys

def solve(s, b):
    n = len(s)
    t = [''] * n
    for i in range(n):
        if b[i] == 0:
            t[i] = s[i]
        else:
            t[i] = '.'
    for i in range(n):
        if t[i] == '.':
            for j in range(n):
                if s[j] == '.':
                    continue
                cnt = 0
                for k in range(n):
                    if t[k] != '.' and s[j] < t[k]:
                        cnt += abs(k - i)
                if cnt == b[i]:
                    t[i] = s[j]
                    s[j] = '.'
                    break
    return ''.join(t)

def main():
    q = int(input())
    for i in range(q):
        s = list(input())
        m = int(input())
        b = list(map(int, input().split()))
        print(solve(s, b))

if __name__ == '__main__':
    main()
