import sys

def get_name(num):
    if num == 1:
        return 'a'
    else:
        num -= 1
        count = 1
        while num > 26 * count:
            num -= 26 * count
            count += 1
        return get_name(count) + get_name(num // count + 1)

def main():
    num = int(sys.stdin.readline())
    print(get_name(num))

if __name__ == '__main__':
    main()
