def main():
    n = int(input())
    a = [int(x) for x in input().split()]
    s = [0]
    for i in range(n):
        s.append(s[i] + a[i])
    m = {}
    for i in range(n + 1):
        if s[i] not in m:
            m[s[i]] = i
        else:
            m[s[i]] = max(m[s[i]], i)
    ans = 0
    for i in range(n + 1):
        ans = max(ans, i - m[s[i]])
    print(n - ans)

