def get_median(l, r, p):
    if (r - l + 1) % 2 == 0:
        return p[(l + r) // 2]
    else:
        return p[(l + r) // 2 + 1]

def main():
    n, m = map(int, input().split())
    p = list(map(int, input().split()))
    cnt = 0
    for i in range(n):
        for j in range(i, n):
            if get_median(i, j, p) == m:
                cnt += 1
    print(cnt)

if __name__ == '__main__':
    main()

import sys

def median(a):
    if len(a) % 2 == 0:
        return (a[len(a) // 2] + a[len(a) // 2 - 1]) // 2
    else:
        return a[len(a) // 2]

def main():
    n, m = map(int, sys.stdin.readline().split())
    p = list(map(int, sys.stdin.readline().split()))
    count = 0
    for i in range(n):
        for j in range(i, n):
            if median(p[i:j+1]) == m:
                count += 1
    print(count)

