import sys

def main():
    n, m = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))

    a.sort(reverse=True)

    ans = 0
    for i in range(n):
        if a[i] <= i + 1:
            break
        ans += a[i] - (i + 1)

    if ans >= m:
        print(n)
    else:
        print(-1)

if __name__ == '__main__':
    main()

def main():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    a.sort(reverse=True)
    days = 0
    while m > 0:
        days += 1
        m -= a[days-1]
        if m <= 0:
            break
        for i in range(days-1):
            a[i] -= 1
            if a[i] < 0:
                a[i] = 0
        a.sort(reverse=True)
    if m > 0:
        print(-1)
    else:
        print(days)

