import sys

def main():
    n, k = [int(x) for x in sys.stdin.readline().split()]
    a = [int(x) for x in sys.stdin.readline().split()]
    a.sort()
    dp = [[0 for _ in range(n)] for _ in range(k)]
    for i in range(n):
        dp[0][i] = i
    for i in range(1, k):
        for j in range(n):
            if j < i:
                dp[i][j] = 0
            else:
                dp[i][j] = dp[i-1][j-1]
                for l in range(dp[i-1][j-1], j):
                    if a[j] - a[l] <= 5:
                        dp[i][j] = max(dp[i][j], dp[i-1][l])
    print(n - dp[k-1][n-1])

if __name__ == "__main__":
    main()

