n, k = map(int, input().split())
a = list(map(int, input().split()))

a.sort()

def count(a, k):
    d = [0] * (10**9 + 1)
    for i in a:
        d[i] += 1

    ans = 0
    for i in range(10**9 + 1):
        ans += min(d[i], k)
        k -= d[i]
        if k <= 0:
            break
    return ans

