if __name__ == '__main__':
    player_count, initial_score, question_count = list(map(int, input().split()))
    players = [initial_score] * player_count
    for i in range(question_count):
        player = int(input())
        players[player - 1] -= 1

        for j in range(player_count):
            if j != player - 1:
                players[j] -= 1

    for player in players:
        print('Yes' if player > 0 else 'No')

