def main():
    d, g = map(int, input().split())
    problems = []
    for i in range(d):
        p, c = map(int, input().split())
        problems.append([p, c])
    ans = 100 * 100
    for i in range(1 << d):
        score = 0
        num = 0
        rest_max = -1
        rest_num = 0
        for j in range(d):
            if (i >> j) & 1:
                score += 100 * (j + 1) * problems[j][0] + problems[j][1]
                num += problems[j][0]
            else:
                rest_max = j
                rest_num = problems[j][0]
        if score >= g:
            ans = min(ans, num)
        else:
            score += 100 * (rest_max + 1)
            num += rest_num
            if score >= g:
                ans = min(ans, num)
            else:
                need = (g - score + 100 * (rest_max + 1) - 1) // (100 * (rest_max + 1))
                ans = min(ans, num + need)
    print(ans)

if __name__ == '__main__':
    main()
