import sys

def main():
    n, m = map(int, sys.stdin.readline().split())
    x = list(map(int, sys.stdin.readline().split()))
    x.sort()
    ans = 0
    for i in range(m):
        if i == 0:
            ans += x[i] - 1
        else:
            ans += min(x[i] - x[i-1], x[i] - x[i-1] - 1)
    print(ans)

if __name__ == '__main__':
    main()

def main():
    N, M = map(int, input().split())
    X = list(map(int, input().split()))
    X.sort()

    ans = 0
    for i in range(M):
        if i == 0:
            ans += X[i] - 0
        else:
            ans += min(X[i] - X[i-1], X[i+1] - X[i]) if i != M-1 else X[i] - X[i-1]

    print(ans)

if __name__ == '__main__':
    main()

def main():
    n, m = map(int, input().split())
    x = list(map(int, input().split()))

    x.sort()
    ans = 0
    for i in range(m):
        if i == 0:
            ans += x[i] - 0
        else:
            ans += x[i] - x[i-1]
    ans += n - 1 - x[m-1]
    print(ans)

if __name__ == '__main__':
    main()
