def main():
    s = input()
    t = input()
    cnt = 0
    while s != '' and t != '':
        if s[0] == t[0]:
            s = s[1:]
            t = t[1:]
        elif s[0] != t[0]:
            if s[0] in t:
                t = t.replace(s[0], '', 1)
                s = s[1:]
            elif t[0] in s:
                s = s.replace(t[0], '', 1)
                t = t[1:]
            else:
                cnt += 1
                s = s[1:]
                t = t[1:]
    cnt += len(s) + len(t)
    print(cnt)

if __name__ == "__main__":
    main()
