import sys

def main():
    t = int(sys.stdin.readline())
    for i in range(t):
        n = int(sys.stdin.readline())
        if n % 2 == 0:
            print(n // 2 - 1)
        else:
            print(n // 2)

if __name__ == '__main__':
    main()

