import sys
import math

def solve(n):
    if n == 1:
        return 0
    else:
        return math.floor((n - 1) / 2)

def main():
    t = int(input())
    for i in range(t):
        n = int(input())
        print(solve(n))

if __name__ == "__main__":
    main()

