import sys

def main():
    data = sys.stdin.readline().strip()
    data = data.split('+')
    print(data)
    for i in range(len(data)):
        if '-' in data[i]:
            data[i] = data[i].split('-')
            for j in range(len(data[i])):
                data[i][j] = int(data[i][j])
        else:
            data[i] = int(data[i])
    print(data)
    out = ''
    for i in range(len(data)):
        if type(data[i]) is int:
            out += '+' * data[i]
            if i != len(data) - 1:
                out += '>'
        else:
            for j in range(len(data[i])):
                out += '+' * data[i][j]
                if j != len(data[i]) - 1:
                    out += '>'
            if i != len(data) - 1:
                out += '>'
    out += '<' * (len(data) - 1)
    out += '[<'
    for i in range(len(data)):
        if type(data[i]) is int:
            out += '+' * data[i]
            if i != len(data) - 1:
                out += '>'
        else:
            for j in range(len(data[i])):
                out += '+' * data[i][j]
                if j != len(data[i]) - 1:
                    out += '>'
            if i != len(data) - 1:
                out += '>'
    out += '-]<'
    out += '.'
    print(out)

if __name__ == "__main__":
    main()

import sys

def main():
    expr = sys.stdin.readline().strip()
    operands = expr.split('+')
    if len(operands) == 1:
        operands = expr.split('-')
    operands = [int(x) for x in operands]
    if len(operands) == 1:
        print('+' * operands[0])
        print('.')
    else:
        print('+' * operands[0])
        print('>')
        for i in range(1, len(operands)):
            if expr[i * 2 - 1] == '+':
                print('+' * operands[i])
            else:
                print('-' * operands[i])
            print('>')
        print('<')
        for i in range(1, len(operands)):
            if expr[i * 2 - 1] == '+':
                print('+' * operands[i])
            else:
                print('-' * operands[i])
            print('<')
        print('[<')
        for i in range(1, len(operands)):
            if expr[i * 2 - 1] == '+':
                print('+' * operands[i])
            else:
                print('-' * operands[i])
            print('>')
        for i in range(1, len(operands)):
            if expr[i * 2 - 1] == '+':
                print('-')
            else:
                print('+')
        print('-]<')
        print('+' * (operands[0] + sum(operands[1:])))
        print('.')

if __name__ == '__main__':
    main()

