def main():
    s = input()
    n = len(s)
    ans = 0
    for i in range(n-1):
        if s[i] == s[i+1]:
            ans += 1
    print(ans)

if __name__ == '__main__':
    main()

import sys

def main():
    s = sys.stdin.readline().rstrip()
    cnt = 0
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            cnt += 1
    print(cnt)

if __name__ == "__main__":
    main()

S = input()

# 全ての色が同じ場合は、1色を変更することで条件を満たす
# 全ての色が異なる場合は、条件を満たす
# それ以外の場合は、色が異なる隣り合うタイルの個数を数える

if S.count('0') == len(S) or S.count('1') == len(S):
    print(1)
else:
    count = 0
    for i in range(len(S)-1):
        if S[i] != S[i+1]:
            count += 1
    print(count)

def main():
    s = input()
    count = 0
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            count += 1
    print(count)

if __name__ == '__main__':
    main()
