import sys

def main():
    n = int(sys.stdin.readline())
    print(int(pow(10, n, 10 ** 9 + 7) - 2))

if __name__ == '__main__':
    main()

N = int(input())

# dp[i][j] := i桁目まで見て、最後の数字がjであるような数列の総数
dp = [[0 for _ in range(10)] for _ in range(N+1)]

for j in range(10):
    dp[1][j] = 1

for i in range(2, N+1):
    for j in range(10):
        dp[i][j] = sum(dp[i-1][:j+1])

print(sum(dp[N]) % (10**9 + 7))

n = int(input())

# dp[i][j][k] = i桁目までで、j=0なら0を含んでいる、j=1なら9を含んでいる、k=0なら未満、k=1なら以上の数字列の総数
dp = [[[0 for _ in range(2)] for _ in range(2)] for _ in range(n+1)]
dp[0][0][0] = 1
dp[0][1][0] = 1

for i in range(1, n+1):
    for j in range(2):
        for k in range(2):
            for l in range(10):
                if j == 0 and l == 0:
                    dp[i][j][k] += dp[i-1][j][k]
                elif j == 1 and l == 9:
                    dp[i][j][k] += dp[i-1][j][k]
                elif k == 0 and l < 9:
                    dp[i][j][k] += dp[i-1][j][k]
                elif k == 1 and l <= 9:
                    dp[i][j][k] += dp[i-1][j][k]
                else:
                    dp[i][j][k] += dp[i-1][j][k]

print(dp[n][0][1] + dp[n][1][1])

import sys

N = int(sys.stdin.readline())

# dp[i][j] = i桁目まででjとなる数の個数
dp = [[0] * 10 for _ in range(N+1)]

for i in range(1, 10):
    dp[1][i] = 1

for i in range(2, N+1):
    for j in range(10):
        if j == 0:
            dp[i][j] = dp[i-1][j+1]
        elif j == 9:
            dp[i][j] = dp[i-1][j-1]
        else:
            dp[i][j] = dp[i-1][j-1] + dp[i-1][j+1]

print(sum(dp[N]) % (10**9 + 7))
