def is_prime(n):
    if n == 1:
        return False
    for i in range(2, int(math.sqrt(n))+1):
        if n % i == 0:
            return False
    return True

def find_next_prime(n):
    if is_prime(n):
        return n
    else:
        return find_next_prime(n+1)

