n = int(input())
v = list(map(int, input().split()))

# Count the number of each element
count = {}
for i in v:
    if i in count:
        count[i] += 1
    else:
        count[i] = 1

# Find the two elements
a = -1
b = -1
for i in count:
    if count[i] > n // 2:
        print(-1)
        exit()
    if count[i] == n // 2:
        if a == -1:
            a = i
        elif b == -1:
            b = i

# If a and b are the same, then no need to replace
if a == b:
    print(0)
    exit()

# Find the number of elements that need to be replaced
ans = 0
for i in range(n - 1):
    if v[i] != a and v[i + 1] != a:
        ans += 1
    if v[i] != b and v[i + 1] != b:
        ans += 1

print(ans // 2)

