n, k = map(int, input().split())
a = list(map(int, input().split()))

a = [(a[i], i) for i in range(n)]
a.sort()

ans = [0] * n

for i in range(n):
    if i % 2 == 0:
        for j in range(max(0, a[i][1] - k), min(n, a[i][1] + k + 1)):
            ans[j] = 1
    else:
        for j in range(max(0, a[i][1] - k), min(n, a[i][1] + k + 1)):
            ans[j] = 2

print(''.join(map(str, ans)))

