    for i in range(1, n + 1):
        a[i] = a[i][1]
    for i in range(1, n + 1):
        if b[a[i]] == 0:
            b[a[i]] = 1
            for j in range(max(1, a[i] - k), min(n, a[i] + k) + 1):
                b[j] = 2
    print(''.join(map(str, b[1:])))

