def main():
    n, k = map(int, input().split())
    skills = list(map(int, input().split()))
    team = [0] * n
    for i in range(n):
        if team[i] == 0:
            team[i] = 1
            for j in range(1, k + 1):
                if i - j >= 0 and team[i - j] == 0:
                    team[i - j] = 1
                if i + j < n and team[i + j] == 0:
                    team[i + j] = 1
    print(''.join(map(str, team)))

