from math import factorial
from collections import defaultdict

def ncr(n, r):
    return factorial(n) // factorial(r) // factorial(n-r)

def solve(n, a):
    d = defaultdict(int)
    for i in a:
        d[i] += 1
    m = max(d.values())
    return ncr(n, m)

if __name__ == '__main__':
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a) % 998244353)

