n, w = map(int, input().split())
a = list(map(int, input().split()))

if a[0] < 0:
    start = abs(a[0])
else:
    start = 0

if a[0] > w:
    print(0)
    exit()

for i in range(1, n):
    if a[i] > 0:
        start += a[i]
    else:
        start -= a[i]
        if start < 0:
            print(0)
            exit()
    if start > w:
        print(0)
        exit()

print(w - start + 1)

