n = int(input())
a = list(map(int, input().split()))

def is_valid(a):
    if len(a) == 1:
        return True
    elif len(a) == 2:
        return a[0] % 3 == 0 and a[1] == a[0] * 2
    else:
        return (a[0] % 3 == 0 and a[1] == a[0] * 2) or (a[0] % 2 == 0 and a[1] == a[0] // 3)

def solve(a):
    if is_valid(a):
        return a
    else:
        if a[0] % 3 == 0 and a[1] == a[0] * 2:
            return [a[1]] + solve(a[2:])
        else:
            return [a[1]] + solve(a[2:])

print(' '.join(map(str, solve(a))))

