N,K = map(int,input().split())

def gcd(a,b):
    if a < b:
        a,b = b,a
    while b != 0:
        a,b = b,a%b
    return a

def lcm(a,b):
    return a*b//gcd(a,b)

def num_of_triples(N,K):
    count = 0
    for a in range(1,N+1):
        for b in range(1,N+1):
            for c in range(1,N+1):
                if (a+b)%K == 0 and (b+c)%K == 0 and (c+a)%K == 0:
                    count += 1
    return count

def num_of_triples_fast(N,K):
    count = 0
    for a in range(1,N+1):
        for b in range(1,N+1):
            c = lcm(K,a+b)//(a+b)
            if c <= N:
                count += N//c
    return count*6

